{
    "scenario" : {
        "name": "Raw scenario with parameters for global vars",
        "gwtType": "ROOT_STEP",
        "steps": [
            {
                "name": "Putting variables with sensitive characters in context",
                "gwtType": "WHEN",
                "steps": [
                    {
                        "type": "context-put",
                        "inputs": {
                            "entries": {
                                "slash": "**escape.slash**"
                            }
                        }
                    },
                    {
                        "type": "context-put",
                        "inputs": {
                            "entries": {
                                "apostrophe": "**testcase parameter apostrophe**"
                            }
                        }
                    },
                    {
                        "type": "context-put",
                        "inputs": {
                            "entries": {
                                "quote": "**testcase parameter quote**"
                            }
                        }
                    },
                    {
                        "type": "context-put",
                        "inputs": {
                            "entries": {
                                "backslash": "**escape.backslash**"
                            }
                        }
                    }
                ]
            },
            {
                "name": "Context contains correct value of those variables",
                "gwtType": "THEN",
                "steps": [
                    {
                        "type": "compare",
                        "inputs": {
                            "mode": "equals",
                            "actual": "${#slash}",
                            "expected": "line with slash as url http:\/\/host:port\/path"
                        }
                    },
                    {
                        "type": "compare",
                        "inputs": {
                            "mode": "equals",
                            "actual": "${#apostrophe}",
                            "expected": "line with apostrophe '"
                        }
                    },
                    {
                        "type": "compare",
                        "inputs": {
                            "mode": "equals",
                            "actual": "${#quote}",
                            "expected": "line with quote \""
                        }
                    },
                    {
                        "type": "compare",
                        "inputs": {
                            "mode": "equals",
                            "actual": "${#backslash}",
                            "expected": "line with backslash \\"
                        }
                    }
                ]
            }
        ]
    }
}
