import { Pipe, PipeTransform } from '@angular/core';
import { intersection } from '@shared/tools/array-utils';

@Pipe({
    name: 'componentSearch'
})
export class ComponentSearchPipe implements PipeTransform {

    transform(input: any, tags: String[], noTag: boolean, all: boolean) {
        return all ? input : input.filter((item: any) => {
            return (this.tagPresent(tags, item) || this.noTagPresent(noTag, item));
        });
    }

    private tagPresent(tags: String[], item: any): boolean {
        return intersection(tags, item.tags).length > 0;
    }

    private noTagPresent(noTag: boolean, item: any): boolean {
        return noTag && item.tags.length === 0;
    }

}

import { Dataset } from '@model';
import { Pipe, PipeTransform } from '@angular/core';
import { containsAll } from '@shared/tools/array-utils';

@Pipe({
    name: 'dataSetSearch'
})
export class DataSetSearchPipe implements PipeTransform {

    transform(input: any, tags: String[]) {
        if (tags.length === 0) {
            return input;
        }

        return input.filter((item: Dataset) => {
            return (this.tagPresent(tags, item));
        });
    }

    private tagPresent(tags: String[], dataset: Dataset): boolean {
        return containsAll(tags, dataset.tags);
    }
}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({ name: 'duration' })
export class DurationPipe implements PipeTransform {
    transform(value: number, mode = 'full'): any {
        const hours = Math.floor(value / 1000 / 60 / 60);
        const minutes = Math.floor((value - hours * 1000 * 60 * 60) / 1000 / 60);
        const seconds = Math.floor((value - hours * 1000 * 60 * 60 - minutes * 1000 * 60) / 1000);
        const milliseconds = value - hours * 1000 * 60 * 60 - minutes * 1000 * 60 - seconds * 1000;

        if (mode === 'full') {
            return this.buildFull(hours, minutes, seconds, milliseconds);
        } else if (mode === 'short') {
            return this.buildShort(hours, minutes, seconds, milliseconds);
        } else {
            throw Error(`InvalidPipeArgument: mode : '${mode}' for pipe DurationPipe`);
        }
    }

    buildFull(hours: number, minutes: number, seconds: number, milliseconds: number) {
        let result = '';

        if (hours > 0) {
            result += hours + 'h ';
        }
        if (minutes > 0) {
            result += minutes + 'm ';
        }
        if (seconds > 0) {
            result += seconds + 's ';
        }
        if (milliseconds > 0) {
            result += milliseconds + 'ms';
        }
        return result.trim();
    }

    buildShort(hours: number, minutes: number, seconds: number, milliseconds: number) {
        if (hours > 0) {
            return hours + ' h';
        } else if (minutes > 0) {
            return minutes + ' m';
        } else if (seconds > 0) {
            return seconds + ' s';
        } else {
            return milliseconds + ' ms';
        }
    }
}

import { Pipe, PipeTransform } from '@angular/core';
import { DomSanitizer, SafeHtml } from '@angular/platform-browser';
import { Linkifier } from '@model';

@Pipe({name: 'linkify'})
export class LinkifyPipe implements PipeTransform {

    constructor(protected sanitizer: DomSanitizer) {}

    transform(value: string, option?: string): SafeHtml {
        const storedLinkifiers = sessionStorage.getItem('linkifiers');
        if (value == null || storedLinkifiers == null) {
            return value ? value : '';
        }

        const linkifiers: Array<Linkifier> = JSON.parse(storedLinkifiers);

        const chunks = this.slice(value, linkifiers, 0);
        const newValue = this.concat(chunks);

        return this.linkify(this.applyOption(newValue, option));
    }

    private slice(value: string, linkifiers: Array<Linkifier>, counter: number): string[] {
        if (value == null || value === '') {
            // @ts-ignore
            return '';
        }

        if (linkifiers[counter] == null) {
            // @ts-ignore
            return value;
        }

        let result;
        let prevIndex = 0;
        const chunks = [];
        const regex = new RegExp(linkifiers[counter].pattern, 'g');
        const regTmp = new RegExp(linkifiers[counter].pattern);

        while ((result = regex.exec(value)) != null) {
            const substr = value.substring(prevIndex, result.index);
            if (substr !== '')  {
                chunks.splice(prevIndex, 0, this.slice(substr, linkifiers, counter + 1));
            }
            prevIndex = regex.lastIndex;
            const match = value.substring(result.index, regex.lastIndex);
            chunks.push(match.replace(regTmp, '<a target="_blank" href="' + linkifiers[counter].link + '">' + result[0] + '</a>'));
        }

        chunks.push(this.slice(value.substring(prevIndex, value.length), linkifiers, counter + 1));

        return chunks;
    }

    private concat(chunks: string[]): string {
        if (typeof chunks === 'string' || chunks instanceof String) {
            // @ts-ignore
            return chunks;
        }

        let slice = '';
        for (const chunk of chunks) {
            // @ts-ignore
            slice += this.concat(chunk);
        }

        return slice;
    }

    private applyOption(value: string, option: string) {
        switch (option) {
            case 'uppercase': return value.toUpperCase();
            default: return value;
        }
    }

    private linkify(value: string): SafeHtml {
        return this.sanitizer.bypassSecurityTrustHtml(value);
    }
}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({name: 'objectAsEntryList'})
export class ObjectAsEntryListPipe implements PipeTransform {
  transform(value, args:string[]): any {
    const keys = [];
    for(const key of Object.getOwnPropertyNames(value)) {
      keys.push({'key': key, value: value[key]});
    }
    return keys;
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import { escapeHtml } from '@shared/tools/string-utils';

@Pipe({ name: 'prettyPrint' })
export class PrettyPrintPipe implements PipeTransform {
  transform(value, escapeHtml: boolean = false): any {
    if (value instanceof Array) {
      return value.map(v => this.beautify(v, escapeHtml)).join('<br/>');
    } else {
      return this.beautify(value, escapeHtml);
    }
  }

  beautify = (content: string, escapeHtmlP: boolean = false) => {
    let r = content;
    try {
      let json = JSON.parse(content);
      if (typeof json === 'string') {
        content = json;
        throw new Error('');
      } else if (Array.isArray(json)) {
          let b = '[';
          for (let i = 0 ; i < json.length ; i++) {
              b += this.beautify(json[i]);
              b += '\n';
          }
          return b + ']';
      }
      return JSON.stringify(json, null, '  ');
    } catch (error) {
      if (content.startsWith('data:image')) {
        return '<img src="' + content + '" />';
      }
      if (content.startsWith('data:')) {
        return '<a href="' + content + '" >download information data</a>';
      }
      if (content.startsWith('<') || content.includes('<?xml')) {
        r = this.formatXml(content, '  ');
      }
    }

    return escapeHtmlP ? escapeHtml(r) : r;
  };

  formatXml = (input, indent) => {
    indent = indent || '\t'; //you can set/define other ident than tabs

    //PART 1: Add \n where necessary
    const xmlString = input
      .replace(/(<([a-zA-Z]+\b)[^>]*>)(?!<\/\2>|[\w\s])/g, "$1\n") //add \n after tag if not followed by the closing tag of pair or text node
      .replace(/(<\/[a-zA-Z]+[^>]*>)/g, "$1\n") //add \n after closing tag
      .replace(/>\s+(.+?)\s+<(?!\/)/g, ">\n$1\n<") //add \n between sets of angled brackets and text node between them
      .replace(/>(.+?)<([a-zA-Z])/g, ">\n$1\n<$2") //add \n between angled brackets and text node between them
      .replace(/\?></, "?>\n<") //detect a header of XML

    const xmlArr = xmlString.split('\n');  //split it into an array (for analise each line separately)

    //PART 2: indent each line appropriately
    let tabs = '';  //store the current indentation
    let start = 0;  //starting line

    if (/^<[?]xml/.test(xmlArr[0])) start++;  //if the first line is a header, ignore it

    for (let i = start; i < xmlArr.length; i++) //for each line
    {
      const line = xmlArr[i].replace(/^\s+|\s+$/g, '');  //trim it (just in case)

      if (/^<[/]/.test(line))  //if the line is a closing tag
      {
        tabs = tabs.replace(indent, '');  //remove one indent from the store
        xmlArr[i] = tabs + line;  //add the tabs at the beginning of the line
      }
      else if (/<.*>.*<\/.*>|<.*[^>]\/>/.test(line))  //if the line contains an entire node
      {
        //leave the store as is
        xmlArr[i] = tabs + line; //add the tabs at the beginning of the line
      }
      else if (/<.*>/.test(line)) //if the line starts with an opening tag and does not contain an entire node
      {
        xmlArr[i] = tabs + line;  //add the tabs at the beginning of the line
        tabs += indent;  //and add one indent to the store
      }
      else  //if the line contain a text node
      {
        xmlArr[i] = tabs + line;  // add the tabs at the beginning of the line
      }
    }

    //PART 3: return formatted string (source)
    return xmlArr.join('\n');  //rejoin the array to a string and return it
  };
}

import { Pipe, PipeTransform } from '@angular/core';
import { DomSanitizer, SafeHtml, SafeStyle, SafeScript, SafeUrl, SafeResourceUrl } from '@angular/platform-browser';

@Pipe({
  name: 'safe'
})
export class SafePipe implements PipeTransform {

  constructor(protected sanitizer: DomSanitizer) {}

  public transform(value: any, type: string): SafeHtml | SafeStyle | SafeScript | SafeUrl | SafeResourceUrl {
    switch (type) {
      case 'html': return this.sanitizer.bypassSecurityTrustHtml(value);
      case 'style': return this.sanitizer.bypassSecurityTrustStyle(value);
      case 'script': return this.sanitizer.bypassSecurityTrustScript(value);
      case 'url': return this.sanitizer.bypassSecurityTrustUrl(value);
      case 'resourceUrl': return this.sanitizer.bypassSecurityTrustResourceUrl(value);
      default: throw new Error(`Invalid safe type specified: ${type}`);
    }
  }
}

