import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';

import { NetworkConfiguration, AgentNetwork, AgentGraphe, Agent, TargetId } from '@model';
import { environment } from '@env/environment';
import { HttpClient } from '@angular/common/http';

@Injectable({
    providedIn: 'root'
})
export class AgentNetworkService {

    constructor(private http: HttpClient) { }

    sendAndSaveConfiguration(configuration: NetworkConfiguration): Observable<Object> {
        return this.http.post(environment.backend + '/api/v1/agentnetwork/configuration', configuration);
    }

    getDescription(): Observable<AgentNetwork> {
        return this.http.get(environment.backend + '/api/v1/description')
            .pipe(map((res: any) => new AgentNetwork(
                new AgentGraphe(res.agentsGraph.agents.map(this.buildAgent)),
                res.networkConfiguration as NetworkConfiguration)
            ));
    }

    /**
     * Build a real {@link Agent} object based on equivalent anonymous structure.
     * @param {Agent} agentDescription casted from anonymous object
     */
    private buildAgent = (agentDescription: Agent) => {
        return new Agent(agentDescription.info, agentDescription.reachableAgents, agentDescription.reachableTargets.map(this.buildTarget));
    }

    /**
     * Build a real {@link TargetId} object based on equivalent anonymous structure.
     * @param {TargetId} targetDescription casted from anonymous object
     */
    private buildTarget = (targetDescription: TargetId) => {
        return new TargetId(targetDescription.name);
    }
}

import { Injectable } from '@angular/core';
import { TranslateService } from '@ngx-translate/core';
import { ToastrService } from 'ngx-toastr';

@Injectable({
  providedIn: 'root'
})
export class AlertService {

  private successTitle: string = '';
  private infoTitle: string = '';
  private errorTitle: string = '';
  private warningTitle: string = '';

  constructor(
    private toastr: ToastrService,
    private translateService: TranslateService
  ) {
    this.initTranslation();
  }

  success(msg: string, config = {}) {
    this.toastr.success(msg, this.successTitle, config);
  }

  info(msg: string, config = {}) {
    this.toastr.info(msg, this.infoTitle, config);
  }

  error(msg: string, config = {}) {
    this.toastr.error(msg, this.errorTitle, config);
  }

  warning(msg: string, config = {}) {
    this.toastr.warning(msg, this.warningTitle, config);
  }

  private initTranslation() {
    this.getTranslation();
    this.translateService.onLangChange.subscribe(() => {
        this.getTranslation();
    });
  }

  private getTranslation() {
    this.translateService.get('alert.success').subscribe((res: string) => {
        this.successTitle = res;
    });
    this.translateService.get('alert.info').subscribe((res: string) => {
        this.infoTitle = res;
    });
    this.translateService.get('alert.error').subscribe((res: string) => {
        this.errorTitle = res;
    });
    this.translateService.get('alert.warning').subscribe((res: string) => {
        this.warningTitle = res;
    });
  }
}

import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';

import { environment } from '@env/environment';
import { Backup } from '@core/model/backups.model';

@Injectable({
    providedIn: 'root'
})
export class BackupsService {

    private url = '/api/v1/backups';

    constructor(private http: HttpClient) {
    }

    public list(): Observable<Array<Backup>> {
        return this.http.get<Array<BackupDto>>(environment.backend + this.url).pipe(
            map(dtos => this.mapToBackups(dtos))
        );
    }

    public get(backup: Backup): Observable<Backup> {
        return this.http.get<BackupDto>(
            environment.backend + this.url + `/${backup.id()}`).pipe(
            map(dto => this.mapToBackup(dto))
        );
    }

    public delete(backup: Backup): Observable<void> {
        return this.http.delete(environment.backend + this.url + `/${backup.id()}`)
            .pipe(map(() => {}));
    }

    public download(backup: Backup): Observable<any> {
        const options: any = {
            responseType: 'arraybuffer'
        };
        return this.http.get(environment.backend + this.url + `/${backup.id()}` + '/download', options);
    }

    public save(backup: BackupDto): Observable<String> {
        return this.http.post(environment.backend + this.url, backup, {responseType: 'text'});
    }

    private mapToBackup(dto: BackupDto): Backup {
        return new Backup(dto.homePage, dto.agentsNetwork, dto.environments, dto.components, dto.globalVars, dto.jiraLinks, dto.time);
    }

    private mapToBackups(dtos: BackupDto[]): Backup[] {
        return dtos.map(dto => this.mapToBackup(dto));
    }
}

export class BackupDto {
    constructor(
        public homePage: boolean,
        public agentsNetwork: boolean,
        public environments: boolean,
        public components: boolean,
        public globalVars: boolean,
        public jiraLinks: boolean,
        public time?: Date) {
    }
}

