import { Execution } from '@core/model/scenario/execution.model';
import { TestCase } from '@core/model/scenario/test-case.model';

export class ScenarioIndex {

    public status;
    public lastExecution;
    public executionCount;
    public isComposed;
    public type;

    constructor(
        public id?: string,
        public title?: string,
        public description?: string,
        public repositorySource?: string,
        public creationDate?: Date,
        public updateDate?: Date,
        public version?: number,
        public author?: string,
        public tags: Array<string> = [],
        public executions?: Array<Execution>,
        public jiraId?: string
    ) {
        this.status = this.findStatus();
        this.lastExecution = this.lastTimeExec();
        this.executionCount = this.countExecutions();
        this.isComposed = this.findIfComposed();
        this.type = this.setType();
    }

    private findStatus() {
        if (this.executions && this.executions.length > 0) {
            return this.executions[0].status;
        } else {
            return 'NOT_EXECUTED';
        }
    }

    private lastTimeExec() {
        if (this.executions && this.executions.length > 0) {
            return this.executions[0].time;
        } else {
            return null;
        }
    }

    private countExecutions() {
        if (this.executions && this.executions.length > 0) {
            return this.executions.length;
        } else {
            return 0;
        }
    }

    private findIfComposed(): boolean {
        return TestCase.isComposed(this.id);
    }

    private setType(): ScenarioType {
        return this.isComposed ? ScenarioType.COMPOSED : ScenarioType.FORM;
    }
}

export enum ScenarioType {
    FORM = 'FORM',
    COMPOSED = 'COMPOSED'
}

import { contains, newInstance } from '@shared/tools/array-utils';

export class SelectableTags<T> {
    private allTags: Array<T> = [];
    private selectedTags: Array<T> = [];
    private noTag = true;

    initialize(allItems: Array<T>) {
        this.allTags = allItems;
        this.selectAll();
    }

    selected(): Array<T> {
        return this.selectedTags;
    }

    all(): Array<T> {
        return this.allTags;
    }

    selectAll() {
        this.selectedTags = newInstance(this.allTags);
        this.noTag = true;
    }

    deselectAll() {
        this.selectedTags = [];
        this.noTag = false;
    }

    isSelected(item: T) {
        return contains(this.selectedTags, item);
    }

    toggleSelect(item: T) {
        if (this.selectedTags.length === this.allTags.length) {
            this.selectedTags = [item];
            this.noTag = false;
        } else {
            if (this.isSelected(item)) {
                this.selectedTags.splice(this.selectedTags.indexOf(item), 1);
                this.selectedTags = newInstance(this.selectedTags);
            } else {
                this.selectedTags.push(item);
                this.selectedTags = newInstance(this.selectedTags);
            }
        }
    }

    selectTags(items: Array<T>) {
        this.selectedTags = items;
    }

    setNoTag(noTag) {
        this.noTag = noTag;
    }

    toggleNoTag() {
        this.noTag = !this.noTag;
    }

    isSelectAll() {
        return this.noTag && this.all().length === this.selected().length;
    }

    isNoTagSelected() {
        return this.noTag;
    }
}

