                path: 'roles',
                loadChildren: () => "import('./modules/roles/roles.module').then(m => m.RolesModule)",
                canActivate: [AuthGuard],
                data: { 'authorizations': [ Authorization.ADMIN_ACCESS ] }
            },
        ]
    }
];

